ON = 1
OFF = 0

def get(ar, x, y)
  return OFF if (x < 0 || y < 0)


  if (ar[x] && ar[x][y])
    ar[x][y]
  else
    OFF
  end
end

def get_light_state(s, x, y)
  return ON if x == 0 && y == 0
  return ON if x == 0 && y == 99
  return ON if x == 99 && y == 0
  return ON if x == 99 && y == 99

  state = get(s, x, y)
  total = get(s, x-1, y-1) + get(s, x, y-1) + get(s, x+1, y-1) + get(s, x-1, y) + get(s, x+1, y) + get(s, x-1, y+1) + get(s, x, y+1) + get(s, x+1,y+1)
  if state == ON
    if total == 2 || total == 3
      ON
    else
      OFF
    end
  elsif state == OFF
    if total == 3
      ON
    else
      OFF
    end
  end
end

